// png.d.ts

// Declare the module type for SVG files
declare module "*.png" {
    const content: string;
    export default content;
  }
  
  // svg.d.ts

// Declare the module type for SVG files
declare module "*.svg" {
  const content: string;
  export default content;
}
 // svg.d.ts

// Declare the module type for SVG files
declare module "*.jpg" {
  const content: string;
  export default content;
}
